import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';

function App() {

  const tasks = [
    { name: "Alice Mayer", task: "Call Sam For payments", priority: "High", avatar: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava2-bg.webp" },
    { name: "Kate Moss", task: "Make payment to Bluedart", priority: "Low", avatar: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava2-bg.webp" },
    { name: "Danny McChain", task: "Office rent", priority: "Medium", avatar: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava3-bg.webp" },
    { name: "Alexa Chung", task: "Office grocery shopping", priority: "High", avatar: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava5-bg.webp" },
    { name: "Ben Smith", task: "Ask for Lunch to Clients", priority: "Low", avatar: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava6-bg.webp" },
  ];


  const [teamMember, setTeamMember] = useState('');
  const [taskDescription, setTaskDescription] = useState('');
  const [priority, setPriority] = useState('High');
  const handleAddTask = (event) => {
    event.preventDefault(); // Verhindert das automatische Neuladen der Seite
    const newTask = {
      name: teamMember,
      task: taskDescription,
      priority: priority,
      avatar: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava2-bg.webp" // Hier kannst du ein Standard-Avatar-Bild verwenden oder es dynamisch setzen
    };
    setTasks([...tasks, newTask]);
    // Zur√ºcksetzen der Formularfelder
    setTeamMember('');
    setTaskDescription('');
    setPriority('High');
  };

  const elements = [];
  for (let i = 0; i < tasks.length; i++) {
    elements.push(
        <tr className="fw-normal" key={i}>
          <th>
            <img
                src={tasks[i].avatar}
                alt={`avatar ${i + 1}`}
                style={{ width: '45px', height: 'auto' }}
            />
            <span className="ms-2">{tasks[i].name}</span>
          </th>
          <td className="align-middle">{tasks[i].task}</td>
          <td className="align-middle">
            <h6 className="mb-0">
            <span className={`badge bg-${tasks[i].priority === 'High' ? 'danger' : tasks[i].priority === 'Medium' ? 'warning' : 'success'}`}>
              {tasks[i].priority} priority
            </span>
            </h6>
          </td>
          <td className="align-middle">
            <a  data-mdb-tooltip-init title="Done"  >
              <i className="fas fa-check fa-lg text-success me-3" style={{backgroundColor:'black' , cursor:'pointer'}}></i>
            </a>
            <a   data-mdb-tooltip-init title="Remove">
              <i className="fas fa-trash-alt fa-lg text-warning" style={{backgroundColor:'black' , cursor:'pointer'}}></i>
            </a>
          </td>
        </tr>
    );
  }

  return (
      <section className="vh-100 gradient-custom-2">

        <div className="container py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-md-12 col-xl-10">

              <div className="card mask-custom">
                <div className="card-body p-4 text-white">

                  <Form style={{ width: '500px' , margin:'0 auto' , marginBottom:'50px' }}>
                    <Form.Group className="mb-3" controlId="formBasicData">

                      <Form.Label>Team Member</Form.Label>
                      <Form.Control type="text" placeholder="Team Member" />

                      <Form.Label>Task</Form.Label>
                      <Form.Control type="text" placeholder="Task" />

                    </Form.Group>


                    <Form.Group className="mb-3" controlId="formPriority">
                    <Dropdown>
                      <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Priority
                      </Dropdown.Toggle>

                      <Dropdown.Menu>
                        <Dropdown.Item >High priority</Dropdown.Item>
                        <Dropdown.Item >Low priority</Dropdown.Item>
                        <Dropdown.Item >Medium priority</Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                    </Form.Group>

                    <Button  variant="primary" >
                      Add
                    </Button>
                  </Form>


                  <table className="table text-white mb-0">
                    <thead>
                    <tr>
                      <th scope="col">Team Member</th>
                      <th scope="col">Task</th>
                      <th scope="col">Priority</th>
                      <th scope="col">Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {elements}
                    </tbody>
                  </table>


                </div>
              </div>

            </div>
          </div>
        </div>
      </section>
  );
}

export default App;
